mapper.select:
select b.article_no, b.user_id, b.title, b.content, b.hit, b.createdat, m.name 
from sharearticle b, user m
where b.article_no = #{articleNo}

mapper.update:
update sharearticle
set hit = hit + 1
where article_no = #{articleNo}

update sharearticle
set title = #{title}, content = #{content}
where article_no = #{articleNo}
